{
	"hash_on": "none",
	"algorithm": "round-robin",
	"name": "guardian-myairtelapp",
	"host_header": null,
	"hash_fallback_header": null,
	"hash_fallback": "none",
	"hash_on_cookie": null,
	"healthchecks": {
		"active": {
			"unhealthy": {
				"http_statuses": [429, 404, 500, 501, 502, 503, 504, 505],
				"tcp_failures": 0,
				"timeouts": 0,
				"http_failures": 0,
				"interval": 0
			},
			"type": "http",
			"http_path": "/",
			"healthy": {
				"successes": 0,
				"interval": 0,
				"http_statuses": [200, 302]
			},
			"https_sni": null,
			"https_verify_certificate": true,
			"timeout": 1,
			"concurrency": 10
		},
		"passive": {
			"unhealthy": {
				"http_failures": 0,
				"http_statuses": [429, 500, 503],
				"tcp_failures": 0,
				"timeouts": 0
			},
			"type": "http",
			"healthy": {
				"successes": 0,
				"http_statuses": [200, 201, 202, 203, 204, 205, 206, 207, 208, 226, 300, 301, 302, 303, 304, 305, 306, 307, 308]
			}
		}
	},
	"hash_on_cookie_path": "/",
	"tags": null,
	"hash_on_header": null,
	"slots": 1000
}

{
	"event": "rate-limit-exceeded",
	"source": "rate-limiting-advanced",
	"handler": "webhook-custom",
	"snooze": null,
	"on_change": null,
	"config": {
		"body_format": true,
		"method": "POST",
		"secret": null,
		"payload_format": true,
		"headers_format": false,
		"payload": {
			"text": "Consumer `{{ consumer.username }}` has exceeded the rate limit of {{ rate }} requests per {{ window }} on service {{ service.name }} ({{ service.host }})"
		},
		"url": "{{slack_webhook_url}}",
		"headers": {
			"content-type": "application/json"
		},
		"body": null,
		"ssl_verify": false
	}
}

